  flexDirection: 'column',
};

const formLabel = {
  marginBottom: '5px',
  fontWeight: 'bold',
  fontSize: '14px',
  color: '#333',
};

const formInput = {
  padding: '10px',
  borderRadius: '4px',
  border: '1px solid #ddd',
