import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import axios from 'axios';
import { isAuthenticated, setupAxiosInterceptors, getUserRole } from './utils/authUtils';

// Pages
import Login from './pages/Login';
import Dashboard from './pages/Dashboard';
import AdminDashboard from './pages/AdminDashboard';
import StudentDashboard from './pages/StudentDashboard';

// Styles
import './styles/App.css';

// Protected route component
const ProtectedRoute = ({ children, allowedRoles = [] }) => {
  const isAuth = isAuthenticated();
  const userRole = getUserRole();

  if (!isAuth) {
    return <Navigate to="/" replace />;
  }

  // Rol kontrolü, eğer rol listesi boşsa herkes erişebilir
  if (allowedRoles.length > 0 && !allowedRoles.includes(userRole)) {
    // Kullanıcının rolüne göre doğru sayfaya yönlendir
    if (userRole === 'Admin') {
      return <Navigate to="/admin" replace />;
    } else if (userRole === 'Student') {
      return <Navigate to="/student" replace />;
    } else {
      return <Navigate to="/dashboard" replace />;
    }
  }

  return children;
};

function App() {
  useEffect(() => {
    // Set up axios interceptors on app load
    setupAxiosInterceptors();

    // Check if token is present and set Authorization header
    const token = localStorage.getItem('token');
    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    }
  }, []);

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Login />} />

        {/* Genel Dashboard - Tüm kullanıcılar için */}
        <Route 
          path="/dashboard" 
          element={
            <ProtectedRoute>
              <Dashboard />
            </ProtectedRoute>
          } 
        />

        {/* Admin Dashboard - Sadece Admin rolü için */}
        <Route 
          path="/admin" 
          element={
            <ProtectedRoute allowedRoles={['Admin']}>
              <AdminDashboard />
            </ProtectedRoute>
          } 
        />

        {/* Öğrenci Dashboard - Sadece Student rolü için */}
        <Route 
          path="/student" 
          element={
            <ProtectedRoute allowedRoles={['Student']}>
              <StudentDashboard />
            </ProtectedRoute>
          } 
        />

        {/* Diğer tüm URL'ler için */}
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </Router>
  );
}

export default App;
