// Backend API Configuration

// Base URL for all API calls - Development environmentında proxy kullanıyoruz
// Production için tam URL kullanılmalı
export const API_BASE_URL = process.env.NODE_ENV === 'development' 
  ? '' // Boş string development modunda proxy kullanır (package.json'daki proxy)
  : 'https://1b07-193-140-250-85.ngrok-free.app';

// Function to get full API URL
export const getApiUrl = (endpoint) => {
  return `${API_BASE_URL}${endpoint.startsWith('/') ? endpoint : '/' + endpoint}`;
};
