import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { getApiUrl, getApiHeaders } from '../config/api';
import StudentTable from '../components/common/StudentTable';

const Dashboard = () => {
  const navigate = useNavigate();
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [debugInfo, setDebugInfo] = useState(null);

  // Pagination state
  const [pageIndex, setPageIndex] = useState(0);
  const [pageSize, setPageSize] = useState(100);
  const [totalPages, setTotalPages] = useState(1);
  const [totalCount, setTotalCount] = useState(0);

  // Kullanıcı ekleme formu state'leri
  const [showAddForm, setShowAddForm] = useState(false);
  const [newStudent, setNewStudent] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    phoneNumber: '',
    studentNumber: '',
    departmentId: '',
    facultyId: ''
  });
  const [departments, setDepartments] = useState([]);
  const [faculties, setFaculties] = useState([]);
  const [addUserLoading, setAddUserLoading] = useState(false);
  const [addUserError, setAddUserError] = useState(null);
  const [addUserSuccess, setAddUserSuccess] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('tokenExpiration');
    navigate('/');
  };

  // Yeni kullanıcı form değişikliği
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewStudent({
      ...newStudent,
      [name]: value
    });
  };

  // Fakülte seçildiğinde ilgili bölümleri getir
  const handleFacultyChange = (e) => {
    const facultyId = e.target.value;
    setNewStudent({
      ...newStudent,
      facultyId: facultyId,
      departmentId: '' // Fakülte değiştiğinde bölüm seçimini sıfırla
    });

    // Seçilen fakülteye ait bölümleri getir
    if (facultyId) {
      const filteredDepartments = departments.filter(dept => dept.facultyId === facultyId);
      setDepartments(filteredDepartments);
    }
  };

  // Öğrenci ekleme işlemi
  const handleAddStudent = async (e) => {
    e.preventDefault();
    try {
      setAddUserLoading(true);
      setAddUserError(null);
      setAddUserSuccess(false);

      const token = localStorage.getItem('token');
      const apiUrl = getApiUrl('/api/Auth/Register');

      const response = await axios({
        method: 'post',
        url: apiUrl,
        headers: getApiHeaders(token),
        data: newStudent,
        timeout: 15000
      });

      if (response.status === 201) {
        setAddUserSuccess(true);
        // Form temizle
        setNewStudent({
          firstName: '',
          lastName: '',
          email: '',
          password: '',
          phoneNumber: '',
          studentNumber: '',
          departmentId: '',
          facultyId: ''
        });

        // Öğrenci listesini yenile
        fetchStudents();
      }
    } catch (err) {
      console.error('Öğrenci ekleme hatası:', err);
      setAddUserError(`Öğrenci eklenirken bir hata oluştu: ${err.message}`);
      if (err.response) {
        console.error('Hata detayları:', err.response.status, err.response.data);
      }
    } finally {
      setAddUserLoading(false);
    }
  };

  // Fakülte ve bölüm verilerini getir
  const fetchFacultiesAndDepartments = async () => {
    try {
      const token = localStorage.getItem('token');

      // Fakülteleri getir
      const facultiesResponse = await axios({
        method: 'get',
        url: getApiUrl('/api/Faculties'),
        headers: getApiHeaders(token),
        timeout: 15000
      });

      // Bölümleri getir
      const departmentsResponse = await axios({
        method: 'get',
        url: getApiUrl('/api/Departments'),
        headers: getApiHeaders(token),
        timeout: 15000
      });

      setFaculties(facultiesResponse.data.items || []);
      setDepartments(departmentsResponse.data.items || []);

    } catch (err) {
      console.error('Fakülte ve bölüm verileri getirme hatası:', err);
    }
  };

  // Öğrenci verilerini doğru yapıya dönüştüren yardımcı fonksiyon
  const processStudentData = (students) => {
    return students.map(student => {
      // Öğrenci için isim oluştur
      let displayName = 'Student';
      if (student.firstName && student.lastName) {
        displayName = `${student.firstName} ${student.lastName}`;
      } else if (student.name) {
        displayName = student.name;
      } else if (student.userName) {
        displayName = student.userName;
      } else if (student.displayName) {
        displayName = student.displayName;
      } else if (student.studentNumber) {
        displayName = `Student ${student.studentNumber}`;
      } else if (student.studentId) {
        displayName = `Student ${student.studentId}`;
      } else if (student.id) {
        displayName = `Student ${student.id}`;
      }

      // E-posta oluştur
      const email = student.email || 
        `${student.studentNumber || student.studentId || 'student'}@university.edu`;

      return {
        ...student,
        email,
        displayName
      };
    });
  };

  // Fetch students from API
  const fetchStudents = async () => {
    try {
      setLoading(true);

      // Get the token from localStorage
      const token = localStorage.getItem('token');

      // API URL'ini consola yazdır
      const apiUrl = getApiUrl(`/api/Students?PageIndex=${pageIndex}&PageSize=${pageSize}`);
      console.log('API URL:', apiUrl);
      console.log('Token:', token ? 'Mevcut' : 'Yok');

      // API çağrısı - yeni headers yapılandırmasını kullan
      const response = await axios({
        method: 'get',
        url: apiUrl,
        headers: getApiHeaders(token),
        responseType: 'json',
        timeout: 15000 // 15 saniye timeout
      });

      console.log('API Response Status:', response.status);
      console.log('API Response Type:', typeof response.data);

      // HTML yanıtı kontrolü
      if (typeof response.data === 'string' && response.data.includes('<!DOCTYPE html>')) {
        console.log('HTML yanıtı alındı, bu bir Ngrok uyarısı olabilir');
        setError('API HTML yanıtı döndü. Ngrok uyarısı atlatılamadı.');
        setStudents([]);
        setLoading(false);
        return;
      }

      // Kısa bir veri önizlemesi göster (çok büyük değilse)
      if (typeof response.data === 'string' && response.data.length < 1000) {
        console.log('API Raw Response:', response.data);
      } else if (typeof response.data === 'object') {
        console.log('API Response Object:', typeof response.data === 'object' ? 'Object received' : 'Not an object');
      }

      let processedData = response.data;

      // Yanıt bir string ise JSON olarak ayrıştırmayı dene
      if (typeof response.data === 'string') {
        try {
          console.log('Response is string, trying to parse as JSON');
          processedData = JSON.parse(response.data);
          console.log('Parsed data:', processedData ? 'Available' : 'Empty');
        } catch (parseError) {
          console.error('Failed to parse response as JSON:', parseError);
          setError('API JSON parsing error: ' + parseError.message);
          setStudents([]);
          setLoading(false);
          return;
        }
      }

      // Handle different response formats
      if (processedData) {
        if (Array.isArray(processedData)) {
          // Direct array response
          console.log('Response contains direct array with', processedData.length, 'items');
          const resultData = processStudentData(processedData);
          setStudents(resultData);
          setTotalCount(processedData.length);
          setTotalPages(Math.ceil(processedData.length / pageSize));
          setError(null);
        } else if (processedData.items && Array.isArray(processedData.items)) {
          // Paginated response with items array
          console.log('Response contains paginated items array with', processedData.items.length, 'items');
          const resultData = processStudentData(processedData.items);
          setStudents(resultData);
          setTotalPages(processedData.pages || 1);
          setTotalCount(processedData.count || 0);
          setError(null);
        } else if (Array.isArray(processedData.data)) {
          // Alternatif API yanıtı formatı
          console.log('Response contains data array with', processedData.data.length, 'items');
          const resultData = processStudentData(processedData.data);
          setStudents(resultData);
          setTotalCount(processedData.data.length);
          setTotalPages(Math.ceil(processedData.data.length / pageSize));
          setError(null);
        } else {
          console.error('Unexpected API response format:', typeof processedData);
          setStudents([]);
          setTotalCount(0);
          setTotalPages(1);
          setError('Unexpected data format received from server');
        }
      } else {
        console.log('No data in response');
        setStudents([]);
        setTotalCount(0);
        setTotalPages(1);
        setError('No data received from server');
      }
    } catch (err) {
      console.error('API call failed:', err);
      console.error('Error details:', err.message);
      if (err.response) {
        console.error('Error response:', err.response.status, err.response.data);
        setDebugInfo({
          errorStatus: err.response.status,
          errorData: JSON.stringify(err.response.data)
        });
      }

      setStudents([]);
      setTotalCount(0);
      setTotalPages(1);
      setError(`API Hatası: ${err.message}`);
    } finally {
      setLoading(false);
    }
  };

  // Fetch students when component mounts or pagination changes
  useEffect(() => {
    fetchStudents();
  }, [pageIndex, pageSize]);

  // Fakülte ve bölüm verilerini sayfa yüklendiğinde bir kez getir
  useEffect(() => {
    fetchFacultiesAndDepartments();
  }, []);

  // Handle pagination change
  const handlePageChange = (newPage) => {
    if (newPage >= 0 && newPage < totalPages) {
      setPageIndex(newPage);
    }
  };

  return (
    <div style={container}>
      <header style={header}>
        <img src="/iyte-logo.png" alt="Logo" style={logoStyle} />
        <h1 style={titleStyle}>Öğrenci Yönetim Sistemi</h1>
        <button onClick={handleLogout} style={logoutBtnStyle}>Çıkış Yap</button>
      </header>

      <main style={main}>
        {error && (
          <div style={{ backgroundColor: '#f8d7da', color: '#721c24', padding: '10px', margin: '10px', borderRadius: '4px' }}>
            {error}
          </div>
        )}

        {debugInfo && (
          <div style={{ backgroundColor: '#e2f3ff', color: '#0c5460', padding: '10px', margin: '10px', borderRadius: '4px', fontSize: '12px' }}>
            <strong>Debug Info:</strong>
            <pre>{JSON.stringify(debugInfo, null, 2)}</pre>
          </div>
        )}

        {/* Öğrenci Ekle Butonu */}
        <div style={{ display: 'flex', justifyContent: 'flex-end', marginBottom: '1rem' }}>
          <button 
            onClick={() => setShowAddForm(!showAddForm)}
            style={addButtonStyle}
          >
            {showAddForm ? 'Formu Kapat' : 'Yeni Öğrenci Ekle'}
          </button>
        </div>

        {/* Öğrenci Ekleme Formu */}
        {showAddForm && (
          <div style={formContainer}>
            <h2 style={{ color: '#7b1fa2', marginBottom: '20px' }}>Yeni Öğrenci Ekle</h2>

            {addUserSuccess && (
              <div style={{ backgroundColor: '#d4edda', color: '#155724', padding: '10px', marginBottom: '20px', borderRadius: '4px' }}>
                Öğrenci başarıyla eklendi!
              </div>
            )}

            {addUserError && (
              <div style={{ backgroundColor: '#f8d7da', color: '#721c24', padding: '10px', marginBottom: '20px', borderRadius: '4px' }}>
                {addUserError}
              </div>
            )}

            <form onSubmit={handleAddStudent}>
              <div style={formRow}>
                <div style={formGroup}>
                  <label htmlFor="firstName" style={formLabel}>Ad</label>
                  <input
                    type="text"
                    id="firstName"
                    name="firstName"
                    value={newStudent.firstName}
                    onChange={handleInputChange}
                    style={formInput}
                    required
                  />
                </div>
                <div style={formGroup}>
                  <label htmlFor="lastName" style={formLabel}>Soyad</label>
                  <input
                    type="text"
                    id="lastName"
                    name="lastName"
                    value={newStudent.lastName}
                    onChange={handleInputChange}
                    style={formInput}
                    required
                  />
                </div>
              </div>

              <div style={formRow}>
                <div style={formGroup}>
                  <label htmlFor="email" style={formLabel}>Email</label>
                  <input
                    type="email"
                    id="email"
                    name="email"
                    value={newStudent.email}
                    onChange={handleInputChange}
                    style={formInput}
                    required
                  />
                </div>
                <div style={formGroup}>
                  <label htmlFor="password" style={formLabel}>Şifre</label>
                  <input
                    type="password"
                    id="password"
                    name="password"
                    value={newStudent.password}
                    onChange={handleInputChange}
                    style={formInput}
                    required
                  />
                </div>
              </div>

              <div style={formRow}>
                <div style={formGroup}>
                  <label htmlFor="studentNumber" style={formLabel}>Öğrenci Numarası</label>
                  <input
                    type="text"
                    id="studentNumber"
                    name="studentNumber"
                    value={newStudent.studentNumber}
                    onChange={handleInputChange}
                    style={formInput}
                    required
                  />
                </div>
                <div style={formGroup}>
                  <label htmlFor="phoneNumber" style={formLabel}>Telefon</label>
                  <input
                    type="text"
                    id="phoneNumber"
                    name="phoneNumber"
                    value={newStudent.phoneNumber}
                    onChange={handleInputChange}
                    style={formInput}
                    required
                  />
                </div>
              </div>

              <div style={formRow}>
                <div style={formGroup}>
                  <label htmlFor="facultyId" style={formLabel}>Fakülte</label>
                  <select
                    id="facultyId"
                    name="facultyId"
                    value={newStudent.facultyId}
                    onChange={handleFacultyChange}
                    style={formInput}
                    required
                  >
                    <option value="">Fakülte Seçin</option>
                    {faculties.map((faculty) => (
                      <option key={faculty.id} value={faculty.id}>
                        {faculty.name}
                      </option>
                    ))}
                  </select>
                </div>
                <div style={formGroup}>
                  <label htmlFor="departmentId" style={formLabel}>Bölüm</label>
                  <select
                    id="departmentId"
                    name="departmentId"
                    value={newStudent.departmentId}
                    onChange={handleInputChange}
                    style={formInput}
                    required
                    disabled={!newStudent.facultyId}
                  >
                    <option value="">Bölüm Seçin</option>
                    {departments
                      .filter(dept => dept.facultyId === newStudent.facultyId)
                      .map((department) => (
                        <option key={department.id} value={department.id}>
                          {department.name}
                        </option>
                      ))}
                  </select>
                </div>
              </div>

              <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
                <button 
                  type="submit" 
                  style={submitButtonStyle}
                  disabled={addUserLoading}
                >
                  {addUserLoading ? 'Kaydediliyor...' : 'Öğrenci Ekle'}
                </button>
              </div>
            </form>
          </div>
        )}

        <StudentTable students={students} loading={loading} />

        {/* Pagination controls */}
        {totalPages > 1 && (
          <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
            <button 
              onClick={() => handlePageChange(pageIndex - 1)} 
              disabled={pageIndex === 0 || loading}
              style={{ margin: '0 10px', padding: '5px 10px' }}
            >
              Önceki
            </button>
            <span style={{ margin: '0 10px', padding: '5px 10px' }}>
              Sayfa {pageIndex + 1} / {totalPages} ({totalCount} öğrenci)
            </span>
            <button 
              onClick={() => handlePageChange(pageIndex + 1)} 
              disabled={pageIndex === totalPages - 1 || loading}
              style={{ margin: '0 10px', padding: '5px 10px' }}
            >
              Sonraki
            </button>
          </div>
        )}
      </main>
    </div>
  );
};

const container = {
  fontFamily: 'Arial, sans-serif',
};

const header = {
  background: 'linear-gradient(to right, #7b1fa2, #a0181b)',
