import React from 'react';
import jsPDF from 'jspdf';
import 'jspdf-autotable';

const StudentTable = ({ students, loading }) => {
  // Ensure students is always an array
  const safeStudents = Array.isArray(students) ? students : [];

  // Debugging info
  console.log('StudentTable received:', safeStudents.length, 'students');
  if (safeStudents.length > 0) {
    console.log('First student example:', safeStudents[0]);
  }

  const exportToPDF = () => {
    const doc = new jsPDF();
    doc.text('Student List', 14, 10);

    const tableColumn = ['Name', 'Email', 'Department', 'Student Number'];
    const tableRows = safeStudents.map((student) => [
      student.name || student.userName || student.displayName || 'N/A',
      student.email || 'N/A',
      student.department || student.departmentName || 'N/A',
      student.studentNumber || student.studentId || student.id || 'N/A',
    ]);

    doc.autoTable({
      head: [tableColumn],
      body: tableRows,
      startY: 20,
      theme: 'grid',
      headStyles: { fillColor: [155, 27, 27] },
    });

    doc.save('student-list.pdf');
  };

  if (loading) {
    return <div>Öğrenciler yükleniyor...</div>;
  }

  if (!safeStudents.length) {
    return <div>Öğrenci bulunamadı. Lütfen API bağlantısını kontrol edin veya sistem yöneticisiyle iletişime geçin.</div>;
  }

  return (
    <div>
      {/* Export Button */}
      <div style={{ display: 'flex', justifyContent: 'flex-end', marginBottom: '1rem' }}>
        <button 
          onClick={exportToPDF} 
          style={exportBtnStyle}
          disabled={loading || !safeStudents.length}
        >
          Export to PDF
        </button>
      </div>

      {/* Student Table */}
      <table style={tableStyle}>
        <thead>
          <tr>
            <th style={thStyle}>Name</th>
            <th style={thStyle}>Email</th>
            <th style={thStyle}>Department</th>
            <th style={thStyle}>Student Number</th>
          </tr>
        </thead>
        <tbody>
          {safeStudents.map((student, i) => (
            <tr key={student.id || i}>
              <td style={tdStyle}>{student.name || student.userName || student.displayName || 'N/A'}</td>
              <td style={tdStyle}>{student.email || 'N/A'}</td>
              <td style={tdStyle}>{student.department || student.departmentName || 'N/A'}</td>
              <td style={tdStyle}>{student.studentNumber || student.studentId || student.id || 'N/A'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

// Table Styles
const tableStyle = {
  width: '100%',
  borderCollapse: 'collapse',
  marginTop: '2rem',
  fontFamily: 'Arial, sans-serif',
};

const thStyle = {
  backgroundColor: '#9B1B1B',
  color: 'white',
  fontWeight: 'bold',
  padding: '12px',
  border: '1px solid black',
  textAlign: 'left',
};

const tdStyle = {
  backgroundColor: '#e0e0e0',
  padding: '12px',
  border: '1px solid black',
  textAlign: 'left',
};

const exportBtnStyle = {
  width: '200px',
  height: '50px',
  padding: '10px 16px',
  backgroundColor: '#7b1fa2',
  color: 'white',
  border: 'none',
  borderRadius: '6px',
  fontWeight: 'bold',
  cursor: 'pointer',
};

export default StudentTable;
