import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { getApiUrl, getApiHeaders } from '../config/api';
import '../styles/main.css';

const Student = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [studentData, setStudentData] = useState(null);
  const [courses, setCourses] = useState([]);
  const [grades, setGrades] = useState([]);

  // Logout handler
  const handleLogout = () => {
    if (window.confirm('Oturumu kapatmak istediğinizden emin misiniz?')) {
      localStorage.removeItem('token');
      localStorage.removeItem('tokenExpiration');
      localStorage.removeItem('userRole');
      localStorage.removeItem('userData');

      delete axios.defaults.headers.common['Authorization'];
      navigate('/');
    }
  };

  // Fetch student information
  const fetchStudentInfo = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');

      // Get student profile data
      const response = await axios({
        method: 'get',
        url: getApiUrl('/api/Students/me'),
        headers: getApiHeaders(token),
        timeout: 15000
      });

      console.log('Student profile data:', response.data);
      setStudentData(response.data);

      // Fetch courses
      await fetchCourses(token);

      // Fetch grades
      await fetchGrades(token);

    } catch (err) {
      console.error('Error fetching student data:', err);
      setError('Öğrenci bilgileri yüklenirken bir hata oluştu.');
    } finally {
      setLoading(false);
    }
  };

  // Fetch courses
  const fetchCourses = async (token) => {
    try {
      const coursesResponse = await axios({
        method: 'get',
        url: getApiUrl('/api/Courses/student'),
        headers: getApiHeaders(token),
        timeout: 15000
      });

      console.log('Student courses:', coursesResponse.data);

      // Process courses data
      let coursesData = [];
      if (coursesResponse.data) {
        if (Array.isArray(coursesResponse.data)) {
          coursesData = coursesResponse.data;
        } else if (coursesResponse.data.items && Array.isArray(coursesResponse.data.items)) {
          coursesData = coursesResponse.data.items;
        }
      }

      setCourses(coursesData);
    } catch (err) {
      console.error('Error fetching courses:', err);
    }
  };

  // Fetch grades
  const fetchGrades = async (token) => {
    try {
      const gradesResponse = await axios({
        method: 'get',
        url: getApiUrl('/api/Grades/student'),
        headers: getApiHeaders(token),
        timeout: 15000
      });

      console.log('Student grades:', gradesResponse.data);

      // Process grades data
      let gradesData = [];
      if (gradesResponse.data) {
        if (Array.isArray(gradesResponse.data)) {
          gradesData = gradesResponse.data;
        } else if (gradesResponse.data.items && Array.isArray(gradesResponse.data.items)) {
          gradesData = gradesResponse.data.items;
        }
      }

      setGrades(gradesData);
    } catch (err) {
      console.error('Error fetching grades:', err);
    }
  };

  useEffect(() => {
    fetchStudentInfo();
  }, []);

  return (
    <div style={styles.container}>
      {/* Header */}
      <header style={styles.header}>
        <div style={styles.logoContainer}>
          <img src="/iyte-logo.png" alt="Logo" style={styles.logo} />
          <h1 style={styles.title}>Graduation Management System</h1>
        </div>
        <div style={styles.userControls}>
          <span style={styles.userInfo}>
            {studentData?.firstName} {studentData?.lastName}
          </span>
          <button 
            onClick={handleLogout} 
            style={styles.logoutButton}
            onMouseEnter={() => document.body.style.cursor = 'pointer'}
            onMouseLeave={() => document.body.style.cursor = 'default'}
          >
            Çıkış Yap
          </button>
        </div>
      </header>

      {/* Main Content */}
      <main style={styles.main}>
        {loading ? (
          <div style={styles.loadingContainer}>
            <div style={styles.spinner}></div>
            <p>Yükleniyor...</p>
          </div>
        ) : error ? (
          <div style={styles.errorContainer}>
            <p style={styles.errorText}>{error}</p>
            <button 
              onClick={fetchStudentInfo} 
              style={styles.retryButton}
            >
              Tekrar Dene
            </button>
          </div>
        ) : (
          <div style={styles.content}>
            {/* Student Profile Card */}
            <div style={styles.card}>
              <h2 style={styles.cardTitle}>Öğrenci Bilgileri</h2>
              <div style={styles.profileInfo}>
                <div style={styles.profileRow}>
                  <span style={styles.label}>Ad Soyad:</span>
                  <span style={styles.value}>{studentData?.firstName} {studentData?.lastName}</span>
                </div>
                <div style={styles.profileRow}>
                  <span style={styles.label}>Öğrenci No:</span>
                  <span style={styles.value}>{studentData?.studentNumber}</span>
                </div>
                <div style={styles.profileRow}>
                  <span style={styles.label}>E-posta:</span>
                  <span style={styles.value}>{studentData?.email}</span>
                </div>
                <div style={styles.profileRow}>
                  <span style={styles.label}>Bölüm:</span>
                  <span style={styles.value}>{studentData?.departmentName}</span>
                </div>
                <div style={styles.profileRow}>
                  <span style={styles.label}>Fakülte:</span>
                  <span style={styles.value}>{studentData?.facultyName}</span>
                </div>
              </div>
            </div>

            {/* Courses Card */}
            <div style={styles.card}>
              <h2 style={styles.cardTitle}>Derslerim</h2>
              {courses.length === 0 ? (
                <p style={styles.emptyMessage}>Henüz kayıtlı ders bulunmamaktadır.</p>
              ) : (
                <div style={styles.tableContainer}>
                  <table style={styles.table}>
                    <thead>
                      <tr>
                        <th style={styles.th}>Ders Kodu</th>
                        <th style={styles.th}>Ders Adı</th>
                        <th style={styles.th}>Kredi</th>
                        <th style={styles.th}>Dönem</th>
                      </tr>
                    </thead>
                    <tbody>
                      {courses.map((course, index) => (
                        <tr key={index} style={index % 2 === 0 ? styles.evenRow : styles.oddRow}>
                          <td style={styles.td}>{course.courseCode}</td>
                          <td style={styles.td}>{course.name}</td>
                          <td style={styles.td}>{course.credits}</td>
                          <td style={styles.td}>{course.semester}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </div>

            {/* Grades Card */}
            <div style={styles.card}>
              <h2 style={styles.cardTitle}>Notlarım</h2>
              {grades.length === 0 ? (
                <p style={styles.emptyMessage}>Henüz not bilgisi bulunmamaktadır.</p>
              ) : (
                <div style={styles.tableContainer}>
                  <table style={styles.table}>
                    <thead>
                      <tr>
                        <th style={styles.th}>Ders Kodu</th>
                        <th style={styles.th}>Ders Adı</th>
                        <th style={styles.th}>Not</th>
                        <th style={styles.th}>Durum</th>
                      </tr>
                    </thead>
                    <tbody>
                      {grades.map((grade, index) => (
                        <tr key={index} style={index % 2 === 0 ? styles.evenRow : styles.oddRow}>
                          <td style={styles.td}>{grade.courseCode}</td>
                          <td style={styles.td}>{grade.courseName}</td>
                          <td style={styles.td}>{grade.grade}</td>
                          <td style={styles.td}>
                            <span style={{
                              ...styles.status,
                              backgroundColor: grade.isPassing ? '#4caf50' : '#f44336'
                            }}>
                              {grade.isPassing ? 'Geçti' : 'Kaldı'}
                            </span>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </div>

            {/* Graduation Status Card */}
            <div style={styles.card}>
              <h2 style={styles.cardTitle}>Mezuniyet Durumu</h2>
              <div style={styles.graduationStatus}>
                <div style={styles.statusItem}>
                  <span style={styles.statusLabel}>Toplam Kredi:</span>
                  <span style={styles.statusValue}>
                    {studentData?.completedCredits || 0} / {studentData?.requiredCredits || 240}
                  </span>
                </div>
                <div style={styles.statusItem}>
                  <span style={styles.statusLabel}>Genel Not Ortalaması:</span>
                  <span style={styles.statusValue}>{studentData?.gpa || '0.00'}</span>
                </div>
                <div style={styles.statusItem}>
                  <span style={styles.statusLabel}>Mezuniyet Durumu:</span>
                  <span style={{
                    ...styles.statusValue,
                    color: studentData?.isEligibleForGraduation ? '#4caf50' : '#f44336'
                  }}>
                    {studentData?.isEligibleForGraduation ? 'Mezun Olabilir' : 'Mezun Olamaz'}
                  </span>
                </div>
              </div>
            </div>
          </div>
        )}
      </main>

      {/* Footer */}
      <footer style={styles.footer}>
        <p>© {new Date().getFullYear()} Graduation Management System. Tüm hakları saklıdır.</p>
      </footer>
    </div>
  );
};

